package org.usfirst.frc.team5114.MyRobot2016.auton.commands;

import edu.wpi.first.wpilibj.Timer;
import edu.wpi.first.wpilibj.command.Command;
import org.usfirst.frc.team5114.MyRobot2016.Robot;

/**
 *
 */
public class DriveNorth extends Command {

    private double power, timeLimit;
    private Timer timer;
    
    
    public DriveNorth(double percentVolt, double seconds) {
        requires(Robot.driveTrain);
        
        timeLimit = seconds;
        power =  percentVolt;
        
        timer = new Timer();
    }

    // Called just before this Command runs the first time
    protected void initialize() {
    	System.out.println("Drive North Command----------");
    	timer.start();
    }

    // Called repeatedly when this Command is scheduled to run
    protected void execute() {
    	Robot.driveTrain.driveNorth(power);
    }

    // Make this return true when this Command no longer needs to run execute()
    protected boolean isFinished() {
        return timer.get() >= timeLimit;
    }

    // Called once after isFinished returns true
    protected void end() {
    	System.out.println("\t-Command isFinished...");
    	Robot.driveTrain.stop();
    	timer.stop();
    }

    // Called when another command which requires one or more of the same
    // subsystems is scheduled to run
    protected void interrupted() {
    	System.out.println("\t-Command interrupted...");
    	Robot.driveTrain.stop();
    	timer.stop();
    }
}
